package com.buct.acmer.controller;import com.buct.acmer.service.CompetitionService;import com.buct.acmer.vo.CompetitionVO;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author * @date 2023-07-16 22:36 */@Api(value = " Competition", tags = " Competition")@RestController@RequestMapping("/api/competition")public class CompetitionController {    @Autowired    private CompetitionService competitionService;    @GetMapping("/number")    @ApiOperation(value = " 比赛场数分析", notes = " 比赛场数分析")    public CompetitionVO getCompetition(){      return competitionService.getCompetition();    }}