package com.buct.acmer.service.impl;import com.buct.acmer.entity.Atcoder;import com.buct.acmer.entity.Codeforces;import com.buct.acmer.service.AtcoderService;import com.buct.acmer.service.CompetitionService;import com.buct.acmer.service.ICodeforcesService;import com.buct.acmer.vo.CompetitionVO;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;/** * @author * @date 2023-07-16 22:42 */@Servicepublic class CompetitionServiceImpl implements CompetitionService {    @Autowired    private AtcoderService atcoderService;    @Autowired    private ICodeforcesService codeforceService;    private static final Integer TWENTY_THREE = 2023;    private static final Integer TEN = 2010;    @Override    public CompetitionVO getCompetition() {        CompetitionVO competitionVO = new CompetitionVO();        List<String> dates = getDates();        List<Long> atcoderList = new ArrayList<>();        List<Long> codeforcesList = new ArrayList<>();        for (String date : dates) {            atcoderList.add(atcoderService.lambdaQuery().like(Atcoder::getStartTimeStamp, date).count());            codeforcesList.add(codeforceService.lambdaQuery().like(Codeforces::getStartTimeStamp, date).count());        }        competitionVO.setDates(dates);        competitionVO.setAtcoderList(atcoderList);        competitionVO.setCodeforcesList(codeforcesList);        return competitionVO;    }    private List<String> getDates() {        List<String> dates = new ArrayList<>();        for (int i = TEN; i <= TWENTY_THREE; i++) {            dates.add(String.valueOf(i));        }        return dates;    }}