package com.buct.spider.job;import com.buct.spider.service.AtcoderService;import com.buct.spider.service.ICodeforcesService;import com.buct.spider.service.StudentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Component;/** * @author * @date 2023-07-16 15:52 */@Componentpublic class ScheduleJob {    @Autowired    private StudentService studentService;    @Autowired    private ICodeforcesService codeforcesService;    @Autowired    private AtcoderService atchesService;    @Scheduled(fixedRate = 18000000)    public void studentCrawling(){        System.out.println("开始爬取学生数据.............................");        studentService.studentCodeForcesCrawling();        studentService.studentAtcoderCrawling();        System.out.println("爬取学生数据完成！");    }    @Scheduled(fixedRate = 18000000)    public void codeforcesCrawling(){        System.out.println("开始爬取Codeforces比赛数据.............................");        codeforcesService.codeforcesCrawling();        System.out.println("爬取Codeforces比赛数据完成！");    }    @Scheduled(fixedRate = 60000)    public void atcoderCrawling(){        System.out.println("开始爬取Atcoder比赛数据.............................");        atchesService.atcoderCrawling();        System.out.println("爬取Atcoder比赛数据完成！");    }}